class Task1 extends React.Component{
    constructor(){
        super();
        this.state={
            Crkicket:"I  love cricket",
            Fan:"Power start pawna kalayan",
            Iam:"   I am naveen kumar"
        }
    }
    upadateDetails=()=>{
        this.setState({
            Crkicket:" I dont have any insterd criket we are my frnds",
            fan:"Rajinikanth fan",
            Iam:"I am Mhaesh kumar"
        })
    }
    render(){
        return(
            <div>
                <button onClick={this.upadateDetails}>upadte</button><br/>
                {this.state.Crkicket}<br/>
                {this.state.Fan}<br/>
                {this.state.Iam}<br/>
            </div>
        )
    }
}
