import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Jsonform from "./jsonform";

function Jsontable(){
    const [data,setData]=useState("")
    useEffect(()=>{
        fetch("http://localhost:3006/Json-curd")
        .then((res)=>{
            return res.json()
        })
        .then((resp)=>{
            setData(resp)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    })
    return(
        <div className="card">
            <div className="card-title">
                <h3>Employee Management system</h3>
            </div>
            
            <div className="card-body">
            <Link to="/Jsonform"><a style={{backgroundColor:"chocolate",padding:"7px",width:"10px"}}>ADD</a></Link>
                <table className="table table-bordered" style={{marginTop:"5%"}}>
                    <thead  className="bg-dark text-light">
                        <tr>
                            <th>Id</th>
                            <th>Title</th>
                            <th>Company</th>
                            <th>Salary</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data &&
                        data.map(item=>(
                            <tr>
                                <td>{item.id}</td>
                                <td>{item.title}</td>
                                <td>{item.company}</td>
                                <td>{item.salary}</td>
                            </tr>

                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}
export default Jsontable;
