import { useState } from "react";


function ChinForm(){
    const [id,setId]=useState("")
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [phone,setPhone]=useState("")

    const chnageId=(e)=>{
        setId(e.target.value)
    }
    const chnageName=(e)=>{
        setName(e.target.value)
    }
    const chnageEmail=(e)=>{
        setEmail(e.target.value)
    }
    const chnagePhone=(e)=>{
        setPhone(e.target.value)
    }

    const handleSubmit=()=>{
        const data={id,name,email,phone}
        fetch("http://localhost:3006/employe",{
            method:"POST",
            headers:{
                "Content-type":"application"
            },
            body:JSON.stringify(data)
        })
        .then(()=>{
            alert("successfully saved")
        })
        .catch((err)=>{
            console.log(err.message)

        })
    }
    return(
        <div>
            <form onSubmit={handleSubmit}>
                <label>ID:</label>
                <input type="text" value={id} onChange={chnageId}/>
                <br/><br/>
                <label>Name:</label>
                <input type="text" value={name} onChange={chnageName}/>
                <br/><br/>
                <label>Email:</label>
                <input type="email" value={email} onChange={chnageEmail}/>
                <br/><br/>
                <label>Mobile no:</label>
                <input type="tel" value={phone} onChange={chnagePhone}/>
                <br/><br/>
                <button onClick={handleSubmit}>Submit</button>
            </form>

        </div>
    )
}
export default ChinForm;
