import React, { useState } from "react"

const APP=()=>{

 const [ inputList, setInputList]= useState (" ");
 const [items,setItems] = useState([]);

 const itemEvent =(e)=>{
    setInputList(e.target.value);

 };

 const listofItems= () => {
   setItems((oldItems) => {
    return[...oldItems,inputList];
   });
 };

 const deleteHandler=(itemvalValue)=>{
    const newItems=items.filter((inputList,itemval)=>itemval !== itemvalValue)
 }

    return(
        <div className="navi">
            <div className="center">
                <br></br>
                <h1>ToDO List</h1>
                <br></br>
                <input type="text" placeholder="" onChange={itemEvent}/>
                <button onClick={listofItems}>ADD</button>

                <ol>
                   {/* { <li>{inputList}</li> } */}

                 {items.map((itemval)=>{
                 return <li>{itemval}&nbsp;&nbsp;&nbsp;<button onClick={()=>deleteHandler(itemval)}>X</button></li> 
                 })}
                </ol>

            </div>

        </div>
    )

}
export default APP;
