import { useState } from "react";

function Navi(){
    const[Fullname,setFullname]=useState()
    const[Email,setEmail]=useState()
    const[Mobile,setMobile]=useState()
    const[City,setCity]=useState()
    const [todo,setTodo]=useState([])
    
    const fullName=(e)=>{
        setFullname(e.target.value)
    }
    const emailNavi=(e)=>{
        setEmail(e.target.value)
    }
    const mobileNavi=(e)=>{
        setMobile(e.target.value)
    }
    const cityNavi=(e)=>{
        setCity(e.target.value)
    }

  const myFunction=(e)=>{
    e.preventDefault()
    const newTodos=[...todo,todo]
    setTodo(newTodos)
  }
 
    return(
        <div>
            <form>
            <label>Full name:</label>
            <input value={Fullname} type="text" placeholder="enter your name" required onChange={fullName}/>
            <br/><br/>
            <label>Email:</label>
            <input  value={Email} type="text" placeholder="email" required onChange={emailNavi}/>
            <br/><br/>
            <label>Mobile:</label>
            <input  value={Mobile} type="text"placeholder="mobile" required onChange={mobileNavi}/>
            <br/><br/>
            <label>City:</label>
            <input value={City} type="text"placeholder="city" required onChange={cityNavi}/>
            <br/><br/>
            <button>ADD</button>
            
            </form>
            <Gui todo={todo}/>
        </div>
    )
}
function Gui({todo}){

    return(
        <div>
            {todo.map((todo,index)=>
            <div key={index}>
                <h3>{todo}</h3>
            </div>)}

        </div>
    )
}

export default Navi;
