import React from "react";

class Navi extends React.Component{
    constructor(){
        super();
        this.State={
            fname:"",
            email:"",
            password:"",
            cpassword:""

        }
    }
    
    navifname=(e)=>{
        this.setState({fname:e.target.value})
    }
    naviemail=(e)=>{
        this.setState({email:e.target.value})
    }
    navipassword=(e)=>{
        this.setState({password:e.target.value})
    }
    navicpassword=(e)=>{
        this.setState({cpassword:e.target.value})
    }





    render(){
        return(
            <div>

                <form>
                    <label>full name:</label>
                    <input value={this.state.fname} type="text" required  onChange={this.navifname}/>
                    <br/><br/>
                    <label>email:</label>
                    <input  value={ this.state.email}type="email" required  onChange={this.naviemail}/>
                    <br/><br/>
                    <label>password:</label>
                    <input value={this.state.password} type="password" required  onChange={this.navipassword}/>
                    <br/><br/>
                    <label>confirm password:</label>
                    <input  value={this.state.cpassword}type="password" required  onChange={this.navicpassword}/>
                    <br/><br/>
                    <input type="submit" />
                </form>


            </div>
        )
    }
}
export default Navi;
