import React from "react";
class Todo extends React.Component{
    constructor(){
        super();
        this.input=React.createRef()
        this.state={
            list:[]
        }
    }
    addTodo=()=>{
        const Item={
        value:this.input.current.value
        }
        if(localStorage.getItem("list")===null){
            const list=[]
            list.push(Item)
            localStorage.setItem("list",JSON.stringify(list))
        }
        else{  
            const list=JSON.parse(localStorage.getItem("list"))
            list.push(Item)
            localStorage.setItem("list",JSON.stringify(list))
        }
        this.setState({
            list:JSON.parse(localStorage.getItem("list"))
        })
    }
    
    componentDidMount(){
        const list=window.localStorage.getItem("list")
        const value=JSON.parse(list)
        if(list === null){
            return false
        }
        else{
            this.setState({
                list:value
            })
        }
    }

    delete=()=>{
        const navi= JSON.parse(localStorage.getItem("list"))
        navi.splice(0,1)
        this.setState({
            list:navi
        })
        localStorage.setItem("list",JSON.stringify(navi))
    }

        
  
    render(){
        return(
            <div>
                <br/>
                <input type="text" ref={this.input} placeholder="Full Name ...?"/>
                <input type="text" ref={this.input} placeholder="Citey...?"/>
                <input type="text" ref={this.input} placeholder="Mobile...?"/>
                <input type="text" ref={this.input} placeholder="Salary  ...?"/>
                <button onClick={this.addTodo}>Add</button>
                <button>Remove all</button>

<table>
    <thead>
        <tr>
            <th>FullName</th>
            <th>City</th>
            <th>Mobile</th>
            <th>Salary</th>
            <th>Action</th>
        </tr>
    </thead>
    
{/* 

                {this.state.list.map((item)=>{
                    
                    return(
                    
                    // <ul key={item}>
                    //      <li>{item.value}
                    //     <button onClick={this.delete}>remove</button>
                    //     </li>
                    // </ul>
                    )
                })} */}


</table>

            </div>
        )
    }
}
export default Todo;
