import React from "react";
class Navi extends React.Component{
    constructor(){
        super();
        this.input=React.createRef()
        this.name=React.createRef()
        this.email=React.createRef()
        this.mobile=React.createRef()
        this.city=React.createRef()

        this.state={
            list:[]
        }
    }

    addData=()=>{
        const Items={value:this.input.current.value,
        value1:this.name.current.value,
        value2:this.email.current.value,
        value3:this.mobile.current.value,
        value4:this.city.current.value }
    
        if(localStorage.getItem("list")=== null){
            const list=[]
                list.push(Items)
                localStorage.setItem("list",JSON.stringify(list))   
        }
        else{
            const list=JSON.parse(localStorage.getItem("list"))
            list.push(Items)
            localStorage.setItem("list",JSON.stringify(list))   

        }
         this.setState({list:JSON.parse(localStorage.getItem("list"))})
    }
     
    componentDidMount(){
        const list=window.localStorage.getItem("list")
        const value=JSON.parse(list)
        if(list == null){
            return false
        }
        else{
            this.setState({list:value})
        }
    }
    render(){
        return(
            <div>
                <input  ref={ this.name} type="text" placeholder="name"/>
                <br/><br/>
                <input  ref={this.email} type="email" placeholder="email"/>
                <br/><br/>
                <input ref={this.mobile} type="text"placeholder="mobile"/>
                <br/><br/>
                <input ref={this.city} type="city" placeholder="city"/>
                <br/><br/>
                <button onClick={this.addData}>ADD</button>

                <thead>
                    <tr>
                        <td>name</td>
                        <td>email</td>
                        <td>mobile</td>
                        <td>city</td>
                    </tr>
                </thead>
                {this.state.list.map((items)=>{return(<div>
                    <tbody>
                        <tr>
                            <td>{items.value}</td>
                            <td>{items.value1}</td>
                            <td>{items.value2}</td>
                            <td>{items.value3}</td>
                            <td>{items.value4}</td>
                        </tr>
                    </tbody>
                </div>)})}
            </div>      
        )
    }
}
export default Navi;
