import React from "react";

class Navi extends React.Component{
    constructor(){
        super();
        this.State={
            fname:"",
            email:"",
            password:"",
            cpassword:""

        }
    }
    
    naviifname=(e)=>{
        this.setState({fname:e.target.value})
    }
    naviiemail=(e)=>{
        this.setState({email:e.target.value})
    }
    naviipassword=(e)=>{
        this.setState({password:e.target.value})
    }
    naviicpassword=(e)=>{
        this.setState({cpassword:e.target.value})
    }


    naviisubmit=()=>{
        if(this.state.fname.length<6){
            alert("full name atlest to enter > 6 character")
        }
        else if( this.state.password != this.state.cpassword){
            alert("password does not match")
        }
        else{
            alert("suceesfully submited"+ this.state.fname + this.state.email)
        }
    }





    render(){
        return(
            <div>

                <form onSubmit={ this.naviisubmit}>
                    <label>full name:</label>
                    <input value={this.state.fname} type="text" required  onChange={this.naviifname}/>
                    <br/><br/>
                    <label>email:</label>
                    <input  value={ this.state.email}type="email" required  onChange={this.naviiemail}/>
                    <br/><br/>
                    <label>password:</label>
                    <input value={this.state.password} type="password" required  onChange={this.naviipassword}/>
                    <br/><br/>
                    <label>confirm password:</label>
                    <input  value={this.state.cpassword}type="password" required  onChange={this.naviicpassword}/>
                    <br/><br/>
                    <input type="submit" />
                </form>


            </div>
        )
    }
}
export default Navi;
