import { useState } from "react";


function Jsonform(){
  const [id,setId]=useState("")
  const [title,setTitle]=useState("")
  const [company,setCompany]=useState("")
  const [salary,setSalary]=useState("")

  const chnageId=(a)=>{
    setId(a.target.value)
  }
  const chnageTitle=(a)=>{
    setTitle(a.target.value)
  }
  const chnageCompany=(a)=>{
    setCompany(a.target.value)
  }
  const chnageSalary=(a)=>{
    setSalary(a.target.value)
  }
  const handelSubmit=()=>{
    const data= [id,title,company,salary]
    fetch("http://localhost:3006/Json-curd",{
    method:"POST",
    headers:{
    "content-type":"application/json"
    },
    body:JSON.stringify(data)
    })
    .then(()=>{
      alert("successfullu submited")
    })
    .catch((err)=>{
      console.log(err.message)
    })
  }
    return(
        <div>
            <form onSubmit={handelSubmit}>
  
  <div className="mb-3">
    <label for="exampleInputPassword1" class="form-label">ID</label>
    <input type="text" value={id}   onChange={chnageId} class="form-control" id="exampleInputPassword1"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputPassword1" class="form-label">title</label>
    <input type="text" value={title} onChange={chnageTitle} class="form-control" id="exampleInputPassword1"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputPassword1" class="form-label">Company</label>
    <input type="text" value={company} onChange={chnageCompany} class="form-control" id="exampleInputPassword1"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputPassword1" class="form-label">Salary</label>
    <input type="text" value={salary}  onChange={ chnageSalary}class="form-control" id="exampleInputPassword1"/>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
          
        </div>
    )
}
export default Jsonform;
