import React from "react";
class Message1 extends React.Component{
    render(){
        const {img,name,city,mobile}=this.props.info
        return(
            <div>
                <img src={img}/><br/>
                name:{name}<br/>
                city:{city}<br/>
                mobile:{mobile}<br/>    
            </div>
        )
    }
}
