import { useState } from "react";



function EmpForm(){
    const [id,setid]=useState=("")
    const [name,setname]=useState=("")
    const [email,setemail]=useState=("")
    const [phone,setphone]=useState=("")

    const changeId=(e)=>{
        setid(e.target.value)
    }
    const changeName=(e)=>{
        setname(e.target.value)
    }
    const changeEmail=(e)=>{
        setemail(e.target.value)
    }
    const changePhone=(e)=>{
        setphone(e.target.value)
    }



    return(
        <div>
          <form>
  <div classname="mb-3">
    <label classname="form-label">ID:</label>
    <input type="text" value={id}  onChange={changeId} disabled="disabled" classname="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
  </div>
  <div classname="mb-3">
    <label  classname="form-label">Name:</label>
    <input type="text"  value={name}  onChange={changeName} classname="form-control" id="exampleInputPassword1"/>
  </div>
  <div classname="mb-3">
    <label  classname="form-label">Email:</label>
    <input type="email" value={email}  onChange={changeEmail} classname="form-control" id="exampleInputPassword1"/>
  </div>
  <div classname="mb-3">
    <label  classname="form-label">phone no:</label>
    <input type="tel" value={phone} onChange={changePhone} classname="form-control" id="exampleInputPassword1"/>
  </div>
  <button type="submit" classname="btn btn-primary">Submit</button>
</form>

        </div>
    )
}
export default EmpForm;
